import React, { useState } from 'react';

// Main App component
function App() {
  // State to hold the fetched fact
  const [fact, setFact] = useState('');
  // State to handle loading state
  const [loading, setLoading] = useState(false);
  // State to handle errors
  const [error, setError] = useState(null);

  // Function to fetch a random cat fact
  const fetchCatFact = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('https://catfact.ninja/fact');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setFact(data.fact);
    } catch (err) {
      setError('Failed to fetch a cat fact.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      backgroundColor: '#1a1a1a',
      color: '#ffffff',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: '2rem',
      fontFamily: 'Arial, sans-serif'
    }}>
      <div style={{
        maxWidth: '600px',
        width: '100%',
        backgroundColor: '#2d2d2d',
        borderRadius: '12px',
        padding: '2rem',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        marginTop: '2rem'
      }}>
        <h1 style={{
          color: '#61dafb',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          textAlign: 'center'
        }}>
          Random Cat Fact
        </h1>
        
        <button 
          onClick={fetchCatFact} 
          disabled={loading}
          style={{
            backgroundColor: '#61dafb',
            color: '#1a1a1a',
            border: 'none',
            padding: '12px 24px',
            borderRadius: '6px',
            fontSize: '1.1rem',
            cursor: loading ? 'not-allowed' : 'pointer',
            opacity: loading ? 0.7 : 1,
            transition: 'all 0.3s ease',
            width: '100%',
            maxWidth: '300px',
            display: 'block',
            margin: '0 auto',
            fontWeight: 'bold'
          }}
          onMouseOver={(e) => e.target.style.backgroundColor = '#4fa8c7'}
          onMouseOut={(e) => e.target.style.backgroundColor = '#61dafb'}
        >
          {loading ? 'Loading...' : 'Get Cat Fact'}
        </button>

        <div style={{
          marginTop: '2rem',
          minHeight: '100px',
          backgroundColor: '#363636',
          borderRadius: '8px',
          padding: '1.5rem',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          {error ? (
            <span style={{ color: '#ff6b6b' }}>{error}</span>
          ) : fact ? (
            <p style={{
              fontSize: '1.2rem',
              lineHeight: '1.6',
              textAlign: 'center',
              margin: 0
            }}>
              {fact}
            </p>
          ) : (
            <p style={{ color: '#888' }}>Click the button to get a random cat fact!</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
