import React, { useState } from 'react';

const terminalTheme = {
  background: '#181a1b', // terminal black
  card: '#23272a', // terminal dark gray
  border: '#444b53',
  text: '#39FF14', // terminal green
  accent: '#FFD700', // yellow for highlights
  buttonBg: '#181a1b',
  buttonText: '#39FF14',
  buttonGlow: '#FFD700',
  buttonHoverBg: '#23272a',
  error: '#ff5555',
  placeholder: '#7afc7a',
  prompt: '#FFD700',
  cursor: '#39FF14',
};

// Main App component
function App() {
  // State to hold the fetched fact
  const [fact, setFact] = useState('');
  // State to handle loading state
  const [loading, setLoading] = useState(false);
  // State to handle errors
  const [error, setError] = useState(null);
  const [isHovered, setIsHovered] = useState(false);
  const [darkMode] = useState(true); // always terminal dark

  // Fetch a random cat fact
  const fetchCatFact = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('https://catfact.ninja/fact');
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setFact(data.fact);
    } catch (err) {
      setError('Failed to fetch a cat fact.');
    } finally {
      setLoading(false);
    }
  };

  // Global style reset
  React.useEffect(() => {
    document.body.style.margin = '0';
    document.body.style.background = terminalTheme.background;
    document.body.style.transition = 'background 0.4s';
  }, []);

  // Blinking cursor effect
  const [showCursor, setShowCursor] = useState(true);
  React.useEffect(() => {
    const interval = setInterval(() => setShowCursor(c => !c), 500);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      minHeight: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: terminalTheme.background,
      fontFamily: 'Fira Mono, Consolas, Menlo, monospace',
      transition: 'background 0.4s',
    }}>
      <div style={{
        maxWidth: 700,
        width: '98%',
        background: terminalTheme.card,
        border: `2.5px solid ${terminalTheme.border}`,
        borderRadius: 8,
        padding: '2.5rem 2rem',
        boxShadow: '0 0 32px 0 #000a',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        transition: 'background 0.4s',
        position: 'relative',
      }}>
        {/* Terminal bar */}
        <div style={{
          width: '100%',
          height: 18,
          display: 'flex',
          alignItems: 'center',
          gap: 6,
          marginBottom: 18,
        }}>
          <span style={{
            display: 'inline-block',
            width: 10, height: 10, borderRadius: '50%', background: '#ff5f56', marginRight: 4
          }}></span>
          <span style={{
            display: 'inline-block',
            width: 10, height: 10, borderRadius: '50%', background: '#ffbd2e', marginRight: 4
          }}></span>
          <span style={{
            display: 'inline-block',
            width: 10, height: 10, borderRadius: '50%', background: '#27c93f'
          }}></span>
        </div>
        <h1 style={{
          color: terminalTheme.text,
          marginBottom: '1.5rem',
          fontSize: '2rem',
          fontWeight: 700,
          letterSpacing: 1,
          textShadow: '0 0 8px #39FF1480',
          fontFamily: 'inherit',
        }}>
          $ cat-facts --random
        </h1>
        <button
          onClick={fetchCatFact}
          disabled={loading}
          style={{
            background: isHovered ? terminalTheme.buttonHoverBg : terminalTheme.buttonBg,
            color: terminalTheme.buttonText,
            border: `2px solid ${terminalTheme.buttonText}`,
            padding: '12px 32px',
            borderRadius: 4,
            fontSize: '1.1rem',
            cursor: loading ? 'not-allowed' : 'pointer',
            opacity: loading ? 0.7 : 1,
            fontWeight: 700,
            marginBottom: 32,
            marginLeft: 0,
            boxShadow: isHovered ? `0 0 16px 4px ${terminalTheme.buttonGlow}` : '0 0 0 rgba(0,0,0,0)',
            outline: 'none',
            fontFamily: 'inherit',
            transition: 'background 0.3s, box-shadow 0.3s',
            letterSpacing: 1,
            textShadow: isHovered ? '0 0 8px #FFD700' : 'none',
          }}
          onMouseOver={() => setIsHovered(true)}
          onMouseOut={() => setIsHovered(false)}
        >
          {loading ? 'Loading...' : 'Get Cat Fact'}
        </button>
        <div style={{
          minHeight: 100,
          background: 'rgba(0,0,0,0.25)',
          borderRadius: 6,
          padding: '1.5rem',
          width: '100%',
          boxSizing: 'border-box',
          marginBottom: 0,
          border: `1.5px solid ${terminalTheme.border}`,
          fontFamily: 'inherit',
        }}>
          {error ? (
            <span style={{ color: terminalTheme.error, fontWeight: 500 }}>{error}</span>
          ) : fact ? (
            <span style={{
              fontSize: '1.15rem',
              lineHeight: 1.7,
              color: terminalTheme.text,
              fontFamily: 'inherit',
              wordBreak: 'break-word',
            }}>
              <span style={{ color: terminalTheme.prompt, fontWeight: 700 }}>&gt; </span>
              {fact}
              {showCursor && <span style={{ color: terminalTheme.cursor, fontWeight: 900 }}>_</span>}
            </span>
          ) : (
            <span style={{ color: terminalTheme.placeholder, fontFamily: 'inherit' }}>
              <span style={{ color: terminalTheme.prompt, fontWeight: 700 }}>&gt; </span>
              Click the button to get a random cat fact!{showCursor && <span style={{ color: terminalTheme.cursor, fontWeight: 900 }}>_</span>}
            </span>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
