import React, { useState } from 'react';

const lightTheme = {
  background: '#f4f6fb',
  card: '#fff',
  text: '#222',
  accent: '#0078ff',
  buttonText: '#fff',
  buttonHover: '#005ecb',
  cardShadow: '0 4px 24px rgba(0,0,0,0.08)',
  factBg: '#f0f4fa',
  error: '#d7263d',
  placeholder: '#888'
};

const darkTheme = {
  background: '#18191a',
  card: '#23272f',
  text: '#fff',
  accent: '#61dafb',
  buttonText: '#18191a',
  buttonHover: '#4fa8c7',
  cardShadow: '0 4px 24px rgba(0,0,0,0.4)',
  factBg: '#363636',
  error: '#ff6b6b',
  placeholder: '#aaa'
};

// Main App component
function App() {
  // State to hold the fetched fact
  const [fact, setFact] = useState('');
  // State to handle loading state
  const [loading, setLoading] = useState(false);
  // State to handle errors
  const [error, setError] = useState(null);
  const [darkMode, setDarkMode] = useState(true);

  const theme = darkMode ? darkTheme : lightTheme;

  // Function to fetch a random cat fact
  const fetchCatFact = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('https://catfact.ninja/fact');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setFact(data.fact);
    } catch (err) {
      setError('Failed to fetch a cat fact.');
    } finally {
      setLoading(false);
    }
  };

  // Global style reset
  React.useEffect(() => {
    document.body.style.margin = '0';
    document.body.style.background = theme.background;
    document.body.style.transition = 'background 0.4s';
  }, [theme.background]);

  return (
    <div style={{
      minHeight: '100vh',
      width: '100vw',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: theme.background,
      transition: 'background 0.4s',
      fontFamily: 'Segoe UI, Arial, sans-serif',
    }}>
      <div style={{
        maxWidth: 600,
        width: '95%',
        background: theme.card,
        borderRadius: 18,
        padding: '2.5rem 2rem',
        boxShadow: theme.cardShadow,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        transition: 'background 0.4s',
      }}>
        <div style={{ width: '100%', display: 'flex', justifyContent: 'flex-end', marginBottom: 12 }}>
          <button
            aria-label="Toggle theme"
            onClick={() => setDarkMode((d) => !d)}
            style={{
              background: 'none',
              border: 'none',
              cursor: 'pointer',
              fontSize: 22,
              color: theme.accent,
              transition: 'color 0.3s',
              outline: 'none',
            }}
            title={darkMode ? 'Switch to light mode' : 'Switch to dark mode'}
          >
            {darkMode ? 'ðŸŒž' : 'ðŸŒ™'}
          </button>
        </div>
        <h1 style={{
          color: theme.accent,
          marginBottom: '2rem',
          fontSize: '2.5rem',
          textAlign: 'center',
          fontWeight: 700,
          letterSpacing: 1
        }}>
          Random Cat Fact
        </h1>
        <button
          onClick={fetchCatFact}
          disabled={loading}
          style={{
            background: theme.accent,
            color: theme.buttonText,
            border: 'none',
            padding: '14px 0',
            borderRadius: 8,
            fontSize: '1.15rem',
            cursor: loading ? 'not-allowed' : 'pointer',
            opacity: loading ? 0.7 : 1,
            width: '100%',
            maxWidth: 320,
            fontWeight: 600,
            margin: '0 auto',
            marginBottom: 32,
            boxShadow: '0 0 0 rgba(0,0,0,0)',
            outline: 'none',
            transition: 'background 0.3s, box-shadow 0.3s',
          }}
          onMouseOver={e => {
            e.target.style.background = theme.buttonHover;
            e.target.style.boxShadow = `0 0 16px 2px ${theme.accent}99`;
          }}
          onMouseOut={e => {
            e.target.style.background = theme.accent;
            e.target.style.boxShadow = '0 0 0 rgba(0,0,0,0)';
          }}
        >
          {loading ? 'Loading...' : 'Get Cat Fact'}
        </button>
        <div style={{
          marginTop: 0,
          minHeight: 100,
          background: theme.factBg,
          borderRadius: 10,
          padding: '1.5rem',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          width: '100%',
          boxSizing: 'border-box',
          transition: 'background 0.4s',
        }}>
          {error ? (
            <span style={{ color: theme.error, fontWeight: 500 }}>{error}</span>
          ) : fact ? (
            <p style={{
              fontSize: '1.2rem',
              lineHeight: 1.6,
              textAlign: 'center',
              margin: 0,
              color: theme.text
            }}>
              {fact}
            </p>
          ) : (
            <p style={{ color: theme.placeholder, textAlign: 'center', margin: 0 }}>
              Click the button to get a random cat fact!
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
